graphql.query:
fragment fundraiserFields on Fundraiser {
    sys {
        id
        publishedAt
        spaceId
        environmentId
        publishedVersion
        firstPublishedAt
    }
    contentfulMetadata {
        tags {
            id
            name
        }
    }
    slug
    organizationName
    donationLink
    contactForLargeDonations {
        json
    }
    description {
        json
    }
    description2 {
        json
    }
    cause
    howWillTheySpendDonations {
        json
    }
    whatWeHaveAccomplished {
        json
    }
    institutionalBacking {
        json
    }
    paymentMethod {
        json
    }
    supportsCryptocurrency
    facebook
    instagram
    organizationWebsite
    smallOrganization
    bannerImage {
        url
        title
        fileName
    }
}

query {
    fundraiser(id:"5zy3mVAEbNg9K7XXNF0Z4a") {
        organizationName
    }

    fundraiserCollection {
        items {
            ...fundraiserFields
        }
    }
}
